import { exec, spawn } from 'child_process';
import { contextBridge, ipcRenderer, IpcRendererEvent } from 'electron';
import path from 'path';
import process, { electron } from 'process';
import { dlog, isDebug } from 'utils/dev';

export type Channels = 'ipc-example';

const getScriptPath = (file_name: string) => {
  if (isDebug()) return `assets/scripts/${file_name}`;
  else {
    return path.join(process.resourcesPath, `assets/scripts/${file_name}`);
  }
};

contextBridge.exposeInMainWorld('electron', {
  ipcRenderer: {
    ...
    state(args: any[], func: (data: string) => void) {
      const scriptPath = getScriptPath('state.py');
      dlog(args[0], args[1]);
      exec(
        `python3 ${scriptPath} ${args[0]} ${args[1]}`,
        (err: any, stdout: any, stderr: any) => {
          dlog(err, stdout, stderr);
          func(stdout);
        }
      );
    },
  },
});

window.electron.ipcRenderer.state(
    [pin['fan'], pin['pump']],
    (data: string) => {
      const json = JSON.parse(data);
      dlog(json, json['data']);
      const success = json['result'] == true;
      if ((success && !isDebug()) || (!success && isDebug())) {
        setOutput({
          fan: {
            ...output['fan'],
            value: json['data'][0] == 1,
          },
          pump: {
            ...output['pump'],
            value: json['data'][1] == 1,
          },
        });
      }
    }
  );
}, []);
